changeSet.sql:
CREATE TABLE IF NOT EXISTS t_role(
id INT PRIMARY KEY AUTO_INCREMENT,
role VARCHAR(125) NOT NULL
);

INSERT INTO customer (username, pass, first_name, last_name, email, t_role_id) VALUES
('Admin', 'Admin', 'Admin', 'Admin', 'Admin', 1),
('User', 'User', 'User', 'User', 'User', 2);

CREATE TABLE IF NOT EXISTS account (
id INT PRIMARY KEY AUTO_INCREMENT,
balance INT,
customer_id VARCHAR(125) NOT NULL,
FOREIGN KEY (customer_id) REFERENCES customer (username)
);
